DROP TABLE IF EXISTS `shops_info`;
CREATE TABLE `shops_info` (`id` int(11) NOT NULL AUTO_INCREMENT,`shop_name` varchar(50) DEFAULT NULL COMMENT '门店名称',`shop_address` varchar(150) DEFAULT NULL COMMENT '门店地址',`shop_tel` varchar(20) DEFAULT NULL COMMENT '门店电话',`account_status` varchar(2) DEFAULT NULL COMMENT '门店状态 运行中0  试用期1 已欠费2 已到期3',`shop_phone` varchar(20) DEFAULT NULL,`fa_title` varchar(100) DEFAULT NULL COMMENT '发票抬头',`fax` varchar(20) DEFAULT NULL COMMENT '传真',`account_end` datetime DEFAULT NULL COMMENT '门店使用截止时间',`boss_id` int(11) DEFAULT NULL COMMENT '老板id',`jdbc_driverclassname` varchar(100) DEFAULT NULL COMMENT '数据库驱动',`jdbc_url` varchar(150) DEFAULT NULL COMMENT '数据库名称',`jdbc_username` varchar(50) DEFAULT NULL COMMENT '数据库用户名',`jdbc_password` varchar(50) DEFAULT NULL COMMENT '数据库密码',`shop_key` varchar(15) DEFAULT NULL COMMENT '门店识别码',`db_name` varchar(20) DEFAULT NULL,`user_count` int(8) DEFAULT '2' COMMENT '员工数量',`wechat_id` int(11) DEFAULT '1' COMMENT '微信公众号id 默认带连途的公众号',PRIMARY KEY (`id`)) ENGINE=InnoDB AUTO_INCREMENT=5 DEFAULT CHARSET=utf8 COMMENT='门店信息表';
